package onbiz.vo;

import java.util.Date;


public class OnBoardFile {
	
	private String bd_fname; // 파일명
	private String bd_pathname; // 경로명
	private Date bd_credte; // 파일 업로드일
	private int bd_no; // 게시물번호 (fk)
	
	public OnBoardFile() {
		super();
		// TODO Auto-generated constructor stub
	}
	public OnBoardFile(String bd_fname) {
		super();
		this.bd_fname = bd_fname;
	}	
	public String getBd_fname() {
		return bd_fname;
	}
	public void setBd_fname(String bd_fname) {
		this.bd_fname = bd_fname;
	}
	public String getBd_pathname() {
		return bd_pathname;
	}
	public void setBd_pathname(String bd_pathname) {
		this.bd_pathname = bd_pathname;
	}
	public Date getBd_credte() {
		return bd_credte;
	}
	public void setBd_credte(Date bd_credte) {
		this.bd_credte = bd_credte;
	}
	public int getBd_no() {
		return bd_no;
	}
	public void setBd_no(int bd_no) {
		this.bd_no = bd_no;
	}
	
}