mapper.update:
update OnCalendar 
set cal_name=#{title}, cal_uptDte=to_char(sysdate,'YYYY-MM-DD'), 
	cal_startDte=#{start}, cal_endDte=#{end}, cal_content=#{content}, 
	cal_bdColor=#{borderColor}, cal_bColor=#{backgroundColor}, 
	cal_tColor=#{textColor}, cal_allDay=#{allDay}
where emp_no=#{emp_no} 
and cal_no=#{id}
