mapper.update:
 UPDATE onrisk
	SET risk_viewcnt = risk_viewcnt+1
WHERE risk_no = #{risk_no}

mapper.select:
select riskfile_name
from risk_file
where risk_no = #{risk_no}

mapper.update:
UPDATE Onrisk
SET risk_name = #{risk_name},
   	   risk_content = #{risk_content},
   	   risk_state = #{risk_state}
   	    WHERE risk_no = #{risk_no}
