package diexp.vo;

import org.springframework.stereotype.Controller;

@Controller
public class Driver {
	private String name;
	private String role;
	public Driver() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Driver(String name, String role) {
		super();
		this.name = name;
		this.role = role;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getRole() {
		return role;
	}
	public void setRole(String role) {
		this.role = role;
	}
	
}
